{#snippet controls(mode: 'light' | 'dark')}
	<label>
		<input type="range" bind:value={fill[mode].lightness} min={0} max={1} step="0.01" />
		fill lightness
		{fill[mode].lightness}
	</label>
	<label>
		<input type="range" bind:value={fill[mode].chroma} min={0} max={0.25} step="0.001" />
		fill chroma
		{fill[mode].chroma}
	</label>
	<label>
		<input type="range" bind:value={background[mode].lightness} min={0} max={1} step="0.01" />
		background lightness
		{background[mode].lightness}
	</label>
	<label>
		<input type="range" bind:value={background[mode].chroma} min={0} max={0.25} step="0.001" />
		background chroma
		{background[mode].chroma}
	</label>
	<label>
		<input type="range" bind:value={outline[mode].lightness} min={0} max={1} step="0.01" />
		outline lightness
		{outline[mode].lightness}
	</label>
	<label>
		<input type="range" bind:value={outline[mode].chroma} min={0} max={0.25} step="0.001" />
		outline chroma
		{outline[mode].chroma}
	</label>
{/snippet}

<main>
	<div>
		{@render logo('light')}
		{@render logo('dark')}
	</div>
	<menu>
		<h1>hue</h1>
		<label>
			<input type="range" bind:value={hue.primary} min={0} max={360} />
			primary
			{hue.primary}
		</label>
		<label>
			<input type="range" bind:value={hue.range} min={0} max={360} />
			range
			{hue.range}
		</label>
		<h1>light mode</h1>
		{@render controls('light')}
		<h1>dark mode</h1>
		{@render controls('dark')}
	</menu>
</main>

<style>
	svg,
	menu {
		width: 100%;
		display: flex;
		flex-flow: column;
		align-items: stretch;
	}

	div {
		display: flex;
		flex-flow: column;
		gap: 1rem;
		padding: 1rem;
		width: 100%;
	}

	label {
		display: flex;
		flex-flow: row;
		align-items: center;
		gap: 1rem;
		width: 500px;
	}

	main {
		display: flex;
		flex-flow: row nowrap;
		width: fit-content;
		margin: auto;
	}

	h1 {
		font-size: large;
	}

	@media (max-aspect-ratio: 1 / 1) {
		main {
			flex-flow: column;
		}
	}
</style>
